import React, { useState, useEffect } from 'react';
import { Flex, Button, Text, useToast } from '@chakra-ui/react';
import Chart from 'chart.js/auto';

import axios from 'axios';


const Dashboard = () => {
    const [selectedFiles, setSelectedFiles] = useState([]);
    const [dataList, setDataList] = useState([null]);
    const [jsonData, setJsonData] = useState(null);
    const toast = useToast();
    const createCharts = (chartId, label, attribute) => {
        const ctx = document.getElementById(chartId).getContext('2d');
        const values = dataList.map(item => item[attribute]);
        const labels = dataList.map((item, index) => `Data ${index + 1}`);
        new Chart(ctx, {
            type: 'bar',
            data: {
                labels: labels,
                datasets: [{
                    label: label,
                    data: values,
                    backgroundColor: 'rgba(75, 192, 192, 0.2)',
                    borderColor: 'rgba(75, 192, 192, 1)',
                    borderWidth: 1
                }]
            },
            options: {
                scales: {
                    y: {
                        beginAtZero: true
                    }
                }
            }
        });
    };
    const handleFileSelect = (event) => {
        const files = event.target.files;
        setSelectedFiles([...files]);
    };
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:8000/api/getdata/');
                const fetchedDataList = response.data;
                setDataList(fetchedDataList);
                if (dataList != null) {
                    createCharts("intensityChart", "Intensity", "intensity");
                    createCharts("relevanceChart", "Likelihood", "likelihood");
                    createCharts("likelihoodChart", "Relevance", "relevance");
                }
            } catch (error) {
                console.error(error);
            }
        };
        fetchData();
    }, []);

    const handleFileSubmit = async () => {
        if (selectedFiles.length > 0) {
            const file = selectedFiles[0];
            if (file.type === 'application/json') {
                const reader = new FileReader();
                reader.onload = async (event) => {
                    const data = JSON.parse(event.target.result);
                    setJsonData(data);
                    try {
                        const response = await axios.post('http://localhost:8000/api/postdata/', { "jsondata": data });
                        toast({
                            title: 'Data Successfully Sent',
                            description: response.data.message,
                            status: 'success',
                            duration: 5000,
                            isClosable: true,
                        });
                    } catch (error) {
                        console.error('Error sending data:', error.response);
                        toast({
                            title: 'Error Sending Data',
                            description: 'An error occurred while sending data.',
                            status: 'error',
                            duration: 5000,
                            isClosable: true,
                        });
                    }
                };
                reader.readAsText(file);
            } else {
                console.log('Selected file is not a JSON file');
                toast({
                    title: 'Invalid File Type',
                    description: 'Please select a valid JSON file.',
                    status: 'error',
                    duration: 5000,
                    isClosable: true,
                });
            }
        } else {
            console.log('No file selected');
            toast({
                title: 'No File Selected',
                description: 'Please select a file before submitting.',
                status: 'warning',
                duration: 5000,
                isClosable: true,
            });
        }
    };


    return (
        <Flex direction="column" align="center" justify="center" height="100vh">
            <Text fontSize="2xl" mb={4}>
                Dashboard
            </Text>
            <input
                type="file"
                accept=".json"
                onChange={handleFileSelect}
                style={{ cursor: 'pointer', marginBottom: '1em' }}
            />
            <Flex direction="column" mt={4}>
                {selectedFiles.map((file, index) => (
                    <Text key={index}>{file.name}</Text>
                ))}
            </Flex>
            <Button colorScheme="teal" mt={4} onClick={handleFileSubmit}>
                Submit
            </Button>

            {(dataList) ? (
                <>
                    <canvas id="intensityChart" width="200" height="100"></canvas>
                    <canvas id="relevanceChart" width="200" height="200"></canvas>
                    <canvas id="likelihoodChart" width="200" height="200"></canvas>
                </>
            ) : (
                <Text>Data Not found</Text>
            )}
        </Flex>
    );
};

export default Dashboard;
